import React from 'react';
import  ContentItem  from '../types/ContentList'

interface ContentListProps {
  content: ContentItem[];
}

export const ContentList: React.FC<ContentListProps> = ({ content }) => {
  return (
    <div className="max-w-5xl mx-auto p-4">
      <h1 className="text-3xl font-bold text-center mb-6 text-gray-800">Content List</h1>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {content.map((item) => (
          <div key={item.id} className="bg-white shadow-md rounded-lg p-4 border border-gray-200">
            <h2 className="text-xl font-semibold text-indigo-600 mb-2">{item.title}</h2>
            <p className="text-gray-700 mb-2">{item.body}</p>
            <p className="text-sm text-gray-500 mb-1">
              <strong>Status:</strong> {item.status}
            </p>
            <p className="text-sm text-gray-500">
              <strong>Tags:</strong> {item.tags?.join(', ') || 'None'}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};
