import React, { useState } from 'react';
import  addContent  from '../api/contentApi';
import './ContentForm.css';

interface ContentData {
    title: string;
    body: string;
    status: "draft" | "review" | "published";
    tags: string[];
}

export default function ContentForm({ onContentAdded }: { onContentAdded: () => void }) {
    const [title, setTitle] = useState("");
    const [body, setBody] = useState("");
    const [status, setStatus] = useState<ContentData['status']>("draft");
    const [tags, setTags] = useState("");

    async function handleSubmit(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault();

        const newContent: ContentData = {
            title,
            body,
            status,
            tags: tags.split(",").map(tag => tag.trim()),
        };

        try {
            await addContent(newContent);
            onContentAdded(); // Refresh the content list after successful submission
            setTitle("");
            setBody("");
            setStatus("draft");
            setTags("");
        } catch (error) {
            console.error("Error adding content:", error);
        }
    }

    return (
        <div className="form-container">
            <h2 className="form-title">Add New Content</h2>
            <form onSubmit={handleSubmit}>
                <label className="form-label"> Title
                    <input
                        className="form-input"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </label>
                <label className="form-label"> Body 
                    <input 
                        className="form-input"
                        value={body} 
                        onChange={(e) => setBody(e.target.value)} 
                    />
                </label>
                <label className="form-label"> Set Status 
                    <select 
                        className="form-select"
                        value={status} 
                        onChange={(e) => setStatus(e.target.value as ContentData['status'])}
                    >
                        <option value="draft">Draft</option>
                        <option value="review">Review</option>
                        <option value="published">Published</option>
                    </select>
                </label>
                <label className="form-label"> Tags 
                    <input 
                        className="form-input"
                        value={tags} 
                        onChange={(e) => setTags(e.target.value)} 
                        placeholder="Enter tags separated by commas"
                    />
                </label>
                <button type='submit' className="submit-button"> Submit</button>
            </form>
        </div>
    );
}
