import React, { useEffect, useState } from 'react';
import  ContentList  from './ContentList';
import { contentApi } from '../api/contentApi';
import ContentItem  from '../types/ContentItem'

const Home: React.FC = () => {
    const [content, setContent] = useState<ContentItem[]>([]); // Correctly typed as an array
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
      const fetchData = async () => {
        try {
          setLoading(true);
          const data = await contentApi();
          setContent(data); // Sets the array of content items to state
          setLoading(false);
        } catch (error) {
          setError('Failed to fetch content');
          setLoading(false);
        }
      };

      fetchData();
    }, []);

    return (
      <div>
        <h1>Content List</h1>
        {loading && <p>Loading...</p>}
        {error && <p>{error}</p>}
        <ContentList content={content} /> {/* Passing the array to ContentList */}
      </div>
    );
  };

  export default Home;
