import React from 'react';
import ContentItem from '../types/ContentList';
import './ContentList.css';

interface ContentListProps {
  content: ContentItem[];
}

export const ContentList: React.FC<ContentListProps> = ({ content }) => {
  return (
    <div className="container">
      <h1 className="title">Content List</h1>
      <div className="content-grid">
        {content.map((item) => (
          <div key={item.id} className="content-card">
            <h2 className="content-title">{item.title}</h2>
            <p className="content-body">{item.body}</p>
            <p className="content-status">
              <strong>Status:</strong> {item.status}
            </p>
            <p className="content-tags">
              <strong>Tags:</strong> {item.tags?.join(', ') || 'None'}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ContentList;
